<?php namespace Surikat;if(isset($_GET['action'])){	switch($_GET['action']){		case 'hashgen':			echo @hash('sha512',"SUPERADMIN={$_POST['login']}:{$_POST['password']}");		break;	}	exit;}?><!DOCTYPE html><head></head><script src="/jquery/surikat.js"></script><body>	<h1>SuperAdmin Config.ini Hash-Generator</h1>	<form>	<label for="login">Login</label><input type="text" name="login"></input><br>	<label for="password">Password</label><input type="text" name="password"></input><br>	<label for="hash">Hash</label><input type="text" name="hash" readonly></input><br>	</form>	<script type="application/javascript">$(function(){	var req = false;	$('input[name="login"],input[name="password"]').keyup(function(){		if($('input[name="login"]').val()&&$('input[name="password"]').val()){			if(req){				req.abort();			}			req = $.post('./superadmin.php?action=hashgen',{login:$('input[name="login"]').val(),password:$('input[name="password"]').val()},function(text){				$('input[name="hash"]').val(text);			});		}		else{			$('input[name="hash"]').val('');		}		$('input[name="hash"]').click(function(){			$(this).select();		});	});	});</script></body></html>